package io.protostuff.examples;

import com.google.common.util.concurrent.Futures;
import com.google.common.util.concurrent.ListenableFuture;

import java.util.Arrays;

/**
 * Service implementation.
 *
 * Interface for {@code SearchService} and all classes for request/responses
 * are generated by protostuff-maven-plugin.
 *
 * @author Konstantin Shchepanovskyi
 */
public class DummySearchService implements SearchService {

    @Override
    public ListenableFuture<SearchResponse> basicSearch(BasicSearchRequest request) {
        SearchResponse response = new SearchResponse();
        response.setResultList(Arrays.asList(
                "result 1",
                "result 2"
        ));
        return Futures.immediateFuture(response);
    }

    @Override
    public ListenableFuture<SearchResponse> extendedSearch(ExtendedSearchRequest request) {
        SearchResponse response = new SearchResponse();
        response.setResultList(Arrays.asList(
                "result 1",
                "result 2",
                "result 3"
        ));
        return Futures.immediateFuture(response);
    }
}
